  return mdx(MDXLayout, _extends({}, layoutProps, props, {
    components: components,
    mdxType: "MDXLayout"
  }), mdx("p", null, "Po rokoch som sa rozhodol od\xEDs\u0165 pre\u010D zo ", mdx("a", _extends({
